import React, { useState } from "react";
import "./Home.css"; // Import styles

const Home = () => {
  const [folder, setFolder] = useState(null);
  const [image, setImage] = useState(null);

  const handleFolderUpload = (event) => {
    setFolder(event.target.files);
  };

  const handleImageUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  const handleScan = () => {
    if (!folder || !image) {
      alert("Please upload both the folder and reference image.");
      return;
    }
    alert("Scanning...");
  };

  return (
    <div className="container">
      <h1 className="title">PersonScan</h1>

      <input type="file" webkitdirectory="true" directory multiple onChange={handleFolderUpload} className="input" />
      <button className="button">Upload Folder</button>

      <input type="file" accept="image/*" onChange={handleImageUpload} className="input" />
      <button className="button">Reference Image</button>

      {image && <img src={image} alt="Reference" className="preview" />}

      <button className="scan-button" onClick={handleScan}>Scan</button>
    </div>
  );
};

export default Home;
