// src/pages/Home.js
import React, { useState } from "react";
import { Card, CardContent, Button, Typography, Grid, Paper } from "@mui/material";

const Home = () => {
  const [folder, setFolder] = useState(null);
  const [image, setImage] = useState(null);
  const [imageList, setImageList] = useState([]);

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    if (files) {
      setFolder(files);
      setImageList(Array.from(files).map((file) => URL.createObjectURL(file)));
    }
  };

  const handleImageSelect = (event) => {
    setImage(event.target.value);
  };

  const handleScan = () => {
    if (!image) {
      alert("Please select an image to scan.");
      return;
    }
    console.log("Scanning image:", image);
    // Add Azure Vision API call here
  };

  return (
    <Card sx={{ maxWidth: 400, margin: "auto", mt: 5, p: 3, textAlign: "center", boxShadow: 3, borderRadius: 2 }}>
      <CardContent>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          PersonaScan
        </Typography>
        <Button variant="contained" color="primary" fullWidth component="label" sx={{ mb: 2, py: 1.5 }}>
          Upload Folder
          <input type="file" webkitdirectory="" directory="" hidden onChange={handleFolderUpload} />
        </Button>
        {/* Display uploaded images as medium icons */}
        <Grid container spacing={2} justifyContent="center" sx={{ mb: 2 }}>
          {imageList.map((src, index) => (
            <Grid item key={index}>
              <Paper sx={{ width: 80, height: 80, overflow: "hidden" }}>
                <img src={src} alt={`uploaded-${index}`} style={{ width: "100%", height: "100%", objectFit: "cover" }} />
              </Paper>
            </Grid>
          ))}
        </Grid>
        {/* Reference Image Selection */}
        {imageList.length > 0 && (
          <Button variant="contained" color="primary" fullWidth component="label" sx={{ mb: 2, py: 1.5 }}>
            Reference Image
            <input type="file" hidden onChange={handleImageSelect} />
          </Button>
        )}
        <Button variant="contained" color="success" fullWidth sx={{ py: 1.5 }} onClick={handleScan}>
          Scan
        </Button>
      </CardContent>
    </Card>
  );
};

export default Home;
