// src/pages/Home.js
import React, { useState } from "react";
import { Card, CardContent, Button, Typography, Grid, Paper } from "@mui/material";

const Home = () => {
  const [folder, setFolder] = useState(null);
  const [imageList, setImageList] = useState([]);
  const [referenceImage, setReferenceImage] = useState(null);

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    if (files) {
      setFolder(files);
      setImageList(Array.from(files).map((file) => URL.createObjectURL(file)));
    }
  };

  const handleImageSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setReferenceImage(URL.createObjectURL(file));
    }
  };

  const handleScan = () => {
    if (!referenceImage) {
      alert("Please select a reference image to scan.");
      return;
    }
    console.log("Scanning image:", referenceImage);
    // Add Azure Vision API call here
  };

  return (
    <Card sx={{ maxWidth: 800, margin: "auto", mt: 5, p: 3, textAlign: "center", boxShadow: 3, borderRadius: 2 }}>
      <CardContent>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          PersonaScan
        </Typography>
        <Button variant="contained" color="primary" fullWidth component="label" sx={{ mb: 2, py: 1.5 }}>
          Upload Folder
          <input type="file" webkitdirectory="" directory="" hidden onChange={handleFolderUpload} />
        </Button>

        {/* Reference Image Selection */}
        {imageList.length > 0 && (
          <Button variant="contained" color="primary" fullWidth component="label" sx={{ mb: 2, py: 1.5 }}>
            Reference Image
            <input type="file" hidden onChange={handleImageSelect} />
          </Button>
        )}
        {/* Display Reference Image as Medium Icon */}
        {referenceImage && (
          <Paper sx={{ width: 100, height: 100, overflow: "hidden", margin: "auto", mb: 2 }}>
            <img src={referenceImage} alt="reference" style={{ width: "100%", height: "100%", objectFit: "cover" }} />
          </Paper>
        )}
        <Button variant="contained" color="success" fullWidth sx={{ py: 1.5 }} onClick={handleScan}>
          Scan
        </Button>
      </CardContent>
    </Card>
  );
};

export default Home;
