// src/pages/Home.js
import React, { useState } from "react";
import { Card, CardContent, Button, Typography, List, ListItem, ListItemText } from "@mui/material";

const Home = () => {
  const [folder, setFolder] = useState(null);
  const [imageList, setImageList] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    if (files) {
      setFolder(files);
      setImageList(Array.from(files).map((file) => file.name));
    }
  };

  const handleImageSelect = (image) => {
    setSelectedImage(image);
  };

  const handleScan = () => {
    if (!selectedImage) {
      alert("Please select an image to scan.");
      return;
    }
    console.log("Scanning image:", selectedImage);
    // Add Azure Vision API call here
  };

  return (
    <Card sx={{ maxWidth: 400, margin: "auto", mt: 5, p: 3, textAlign: "center", boxShadow: 3, borderRadius: 2 }}>
      <CardContent>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          PersonaScan
        </Typography>
        <Button variant="contained" color="primary" fullWidth component="label" sx={{ mb: 2, py: 1.5 }}>
          Upload Folder
          <input type="file" webkitdirectory="" directory="" hidden onChange={handleFolderUpload} />
        </Button>
        {imageList.length > 0 && (
          <>
            <Typography variant="h6" gutterBottom>
              Uploaded Files:
            </Typography>
            <List sx={{ maxHeight: 150, overflow: "auto", mb: 2 }}>
              {imageList.map((image, index) => (
                <ListItem button key={index} onClick={() => handleImageSelect(image)} selected={selectedImage === image}>
                  <ListItemText primary={image} />
                </ListItem>
              ))}
            </List>
          </>
        )}
        <Button variant="contained" color="success" fullWidth sx={{ py: 1.5 }} onClick={handleScan}>
          Scan
        </Button>
      </CardContent>
    </Card>
  );
};

export default Home;
