import React, { useState } from "react";
import { Card, CardContent } from "@mui/material";
import FileUpload from "../components/FileUpload";
import ImageSelect from "../components/ImageSelect";
import ScanButton from "../components/ScanButton";
import Header from "../components/Header";

const Home = () => {
  const [folder, setFolder] = useState(null);
  const [image, setImage] = useState(null);
  const [imageList, setImageList] = useState([]);

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    if (files) {
      setFolder(files);
      setImageList(Array.from(files).map((file) => file.name));
    }
  };

  const handleImageSelect = (event) => {
    setImage(event.target.value);
  };

  const handleScan = () => {
    if (!image) {
      alert("Please select an image to scan.");
      return;
    }
    console.log("Scanning image:", image);
    // Add Azure Vision API call here
  };

  return (
    <Card sx={{ maxWidth: 400, margin: "auto", mt: 5, p: 2, textAlign: "center" }}>
      <CardContent>
        <Header />
        <FileUpload onUpload={handleFolderUpload} />
        {imageList.length > 0 && <ImageSelect imageList={imageList} onSelect={handleImageSelect} />}
        <ScanButton onScan={handleScan} />
      </CardContent>
    </Card>
  );
};

export default Home;
